import sys

def O(s, sep='\n'): sys.stdout.write(str(s) + sep)
def I(): return sys.stdin.readline().replace('\n', '')
def MI(): return list(map(int, sys.stdin.readline().replace('\n', '').split()))

def SumOfRoundNumbers():
    # OUTPUT
    # ln. 1: len(res)
    # ln. 2: res.append(get ls index of "i" (go up), multiply by 10^i with "i" going down for each iteration); O(res)
    n = int(I())
    for _ in range(n):s = I();ls = list(s);res = [];[res.append(int( ls[i] ) * ( 10 ** ( len(ls) - i - 1 ) )) if int( ls[i] ) * ( 10 ** ( len(ls) - i - 1 ) ) > 0 else '' for i in range(0, len(s))];O(len(res));O(" ".join(map(str, res)))


if __name__ == '__main__':
    SumOfRoundNumbers()